def Write(rows):

    def return_str(row):
        return "|".join(map(str, [f'"{item}"'
        for item in row.values()]))+'\n'


    header = "|".join(map(str,[f'"{key}"' for key in rows[0].keys()]))

    with open("CoStar_INV_IS_US_2020.txt", 'w') as file:
        file.write(header+'\n')
        file.writelines("".join([return_str(row)
                                for row in rows]))

